// Get the original visualization code string from the input
const originalVisualizationCode = $input.all()[0]?.json?.output;

// Get the data from your BigQuery node
const bigQueryData = $("Google BigQuery")
  .all()
  .map((item) => item.json);

// Initialize a variable for the trimmed code
let trimmedVisualizationCode = '';

// Safety Check: Only trim the code if it's a valid string
if (typeof originalVisualizationCode === 'string') {
  // Use slice(10, -3) to remove the first 10 and last 3 characters
  trimmedVisualizationCode = originalVisualizationCode.slice(10, -3);
}

// Construct the final output string using the trimmed code
const outputText = `df = pd.DataFrame(${JSON.stringify(bigQueryData)})\n${trimmedVisualizationCode}`;

// Return the final text
return { outputText };

const data = $input.all().map((item) => item.json);

const dtypes = Object.keys(data[0]).reduce((acc, key) => {
  acc[key] = typeof data[0][key];
  return acc;
}, {});

const head = data.slice(0, 5);

const result = {
  dtypes: JSON.stringify(dtypes),
  head: JSON.stringify(head),
};

// Return in n8n format - array of items with json property
return [{ json: result }];

const items = $input.all();

const messages = items.map((item) => {
  const parsed = JSON.parse(item.json.body);
  return parsed.message;
});

return [{ json: { messages } }];
